	saveData[levelCompleteKey] = "true";
	server.UpdateUserInternalData(
	{
		PlayFabId: currentPlayerId,
		Data: saveData
	});

	// Return the results of the item grant so the client can see what they got
	return {
		rewards: resultItems
	};
}

